package org.springframework.social.showcase.config;

import javax.sql.DataSource;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.security.crypto.encrypt.TextEncryptor;
import org.springframework.social.connect.jdbc.JdbcConnectionRepository;
import org.springframework.social.connect.support.ConnectionRepository;
import org.springframework.social.facebook.connect.FacebookServiceProvider;
import org.springframework.social.tripit.connect.TripItServiceProvider;
import org.springframework.social.twitter.connect.TwitterServiceProvider;
import org.springframework.stereotype.Component;

@Configuration
public class ServiceProviders {

    @Bean
    public TwitterServiceProvider twitter(@Value("#{environment['twitter.clientKey']}") String clientKey,
            @Value("#{environment['twitter.clientSecret']}") String clientSecret, ConnectionRepository connectionRepository) {
        return new TwitterServiceProvider(clientKey, clientSecret, connectionRepository);
    }

    @Bean
    public FacebookServiceProvider facebook(@Value("#{environment['facebook.apiKey']}") String clientId,
            @Value("#{environment['facebook.appSecret']}") String clientSecret, ConnectionRepository connectionRepository) {
        return new FacebookServiceProvider(clientId, clientSecret, connectionRepository);
    }

    @Bean
    public TripItServiceProvider tripit(@Value("#{environment['tripit.clientKey']}") String clientKey,
            @Value("#{environment['tripit.clientSecret']}") String clientSecret, ConnectionRepository connectionRepository) {
        return new TripItServiceProvider(clientKey, clientSecret, connectionRepository);
    }

    @Bean
    public ConnectionRepository connectionRepository(DataSource dataSource, TextEncryptor textEncryptor) {
        return new JdbcConnectionRepository(dataSource, textEncryptor);
    }

}

package org.springframework.social.showcase.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.context.annotation.Profile;
import org.springframework.security.crypto.encrypt.Encryptors;
import org.springframework.security.crypto.encrypt.TextEncryptor;

@Configuration
@Profile("dev")
public class NoOpTextEncryptor {

    @Bean
    public TextEncryptor textEncryptor() {
        return Encryptors.noOpText();
    }

}

package org.springframework.social.showcase.config;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.context.annotation.Profile;
import org.springframework.security.crypto.encrypt.Encryptors;
import org.springframework.security.crypto.encrypt.TextEncryptor;

@Configuration
@Profile("production")
public class QueryableTextEncryptor {

    @Bean
    public TextEncryptor textEncryptor(@Value("#{environment['encrypt.password']}") String password,
            @Value("#{environment['encrypt.salt']}") String salt) {
        return Encryptors.queryableText(password, salt);
    }

}

package org.springframework.social.showcase.config;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.social.web.connect.ConnectController;
import org.springframework.stereotype.Component;

@Configuration
public class ConnectControllers {

    @Bean
    public ConnectController connectController(@Value("#{environment['application.url']}") String applicationUrl) {
        return new ConnectController(applicationUrl);
    }

}

public interface ConnectInterceptor<S> {
    void preConnect(ServiceProvider<S> provider, WebRequest request);
    void postConnect(ServiceProvider<S> provider, ServiceProviderConnection<S> connection, WebRequest request);
}

package org.springframework.social.showcase.twitter;
import org.springframework.social.connect.ServiceProvider;
import org.springframework.social.connect.ServiceProviderConnection;
import org.springframework.social.twitter.DuplicateTweetException;
import org.springframework.social.twitter.TwitterOperations;
import org.springframework.social.web.connect.ConnectInterceptor;
import org.springframework.util.StringUtils;
import org.springframework.web.context.request.WebRequest;

public class TweetAfterConnectInterceptor implements ConnectInterceptor<TwitterOperations> {

    public void preConnect(ServiceProvider<TwitterOperations> provider, WebRequest request) {
        // nothing to do
    }

    public void postConnect(ServiceProvider<TwitterOperations> provider, ServiceProviderConnection<TwitterOperations> connection, WebRequest request) {
        connection.getServiceApi().updateStatus("I've connected with the Spring Social Showcase!");
    }

}

@Bean
public ConnectController connectController(@Value("#{environment['application.url']}") String applicationUrl) {
    ConnectController connectController = new ConnectController(applicationUrl);
    List<ConnectInterceptor<?>> interceptors = new ArrayList<ConnectInterceptor<?>>();
    interceptors.add(new TweetAfterConnectInterceptor());
    connectController.setInterceptors(interceptors);
    return connectController;
}
